  }

  public render(ctx: CanvasRenderingContext2D) {
    ctx.fillStyle = this.definition.color;
    ctx.fillRect(this.x - 10, this.y - 10, 20, 20);

    // Add glow effect
    ctx.shadowColor = this.definition.color;
    ctx.shadowBlur = 10;
    ctx.fillRect(this.x - 8, this.y - 8, 16, 16);
    ctx.shadowBlur = 0;
  }
}

