  }
}

client/src/lib/game/managers/WaveManager.content:
import { Enemy } from "../entities/Enemy";

export class WaveManager {
  private currentWave = 1;
  private enemiesSpawned = 0;
  private enemiesPerWave = 5;
  private spawnTimer = 0;
  private spawnInterval = 2; // seconds between spawns
  private waveStartTime = 0;
  private waveDuration = 30; // seconds per wave

  public update(deltaTime: number) {
    this.waveStartTime += deltaTime;
    this.spawnTimer += deltaTime;
  }

  public spawnEnemies(canvasWidth: number, canvasHeight: number): Enemy[] {
    const enemies: Enemy[] = [];

    // Check if we should advance to next wave
    if (this.waveStartTime >= this.waveDuration) {
      this.advanceWave();
    }

    // Spawn enemies based on timer
    if (this.spawnTimer >= this.spawnInterval && this.enemiesSpawned < this.getEnemiesForCurrentWave()) {
      const enemy = this.createRandomEnemy(canvasWidth, canvasHeight);
      enemies.push(enemy);
      this.enemiesSpawned++;
      this.spawnTimer = 0;
    }

    return enemies;
  }

  private advanceWave() {
    this.currentWave++;
    this.enemiesSpawned = 0;
    this.waveStartTime = 0;
    this.spawnInterval = Math.max(0.5, this.spawnInterval * 0.95); // Spawn faster each wave
    console.log(`Wave ${this.currentWave} started!`);
  }

  private getEnemiesForCurrentWave(): number {
    return this.enemiesPerWave + Math.floor(this.currentWave / 2);
  }

  private createRandomEnemy(canvasWidth: number, canvasHeight: number): Enemy {
    // Spawn from random edge
    let x, y;
    const edge = Math.floor(Math.random() * 4);
    const margin = 50;

    switch (edge) {
      case 0: // top
        x = Math.random() * canvasWidth;
        y = -margin;
        break;
      case 1: // right
        x = canvasWidth + margin;
        y = Math.random() * canvasHeight;
        break;
      case 2: // bottom
        x = Math.random() * canvasWidth;
        y = canvasHeight + margin;
        break;
      case 3: // left
        x = -margin;
        y = Math.random() * canvasHeight;
        break;
      default:
        x = canvasWidth / 2;
        y = -margin;
    }

    // Determine enemy type based on wave
    let type: "basic" | "fast" | "tank" = "basic";
    const rand = Math.random();

    if (this.currentWave >= 3) {
      if (rand < 0.2) type = "fast";
      else if (rand < 0.1) type = "tank";
    } else if (this.currentWave >= 2) {
      if (rand < 0.15) type = "fast";
    }

    return new Enemy(x, y, type);
  }

  public getCurrentWave(): number {
    return this.currentWave;
  }

  public reset() {
    this.currentWave = 1;
    this.enemiesSpawned = 0;
    this.spawnTimer = 0;
    this.waveStartTime = 0;
    this.spawnInterval = 2;
  }
}

client/src/lib/game/rendering/AnimationM.content:
export interface AnimationFrame {
  x: number;
  y: number;
  width: number;
  height: number;
}

export interface AnimationDefinition {
  frames: AnimationFrame[];
  frameDuration: number; // seconds per frame
  loop: boolean;
}

export class AnimationManager {
  private animations: Map<string, AnimationDefinition> = new Map();
  private currentAnimations: Map<string, {
    currentFrame: number;
    elapsed: number;
    playing: boolean;
  }> = new Map();

  public addAnimation(name: string, frames: AnimationFrame[], frameDuration: number = 0.1, loop: boolean = true) {
    this.animations.set(name, {
      frames,
      frameDuration,
      loop
    });
  }

  public startAnimation(animationName: string, instanceId: string) {
    if (this.animations.has(animationName)) {
      // Always reset the animation state when starting a new animation
      this.currentAnimations.set(instanceId, {
        currentFrame: 0,
        elapsed: 0,
        playing: true
      });
    }
  }

  public update(deltaTime: number, instanceId: string, animationName: string): AnimationFrame | null {
    const animation = this.animations.get(animationName);
    let state = this.currentAnimations.get(instanceId);

    if (!animation) {
      console.warn(`Animation ${animationName} not found`);
      return null;
    }

    // Ensure deltaTime is a valid number
    const validDeltaTime = typeof deltaTime === 'number' && !isNaN(deltaTime) ? deltaTime : 0.016; // fallback to ~60fps

    if (typeof deltaTime !== 'number' || isNaN(deltaTime)) {
      console.warn(`Invalid deltaTime received: ${typeof deltaTime} (${deltaTime}), using fallback`);
    }

    // Initialize state if it doesn't exist
    if (!state) {
      state = {
        currentFrame: 0,
        elapsed: 0,
        playing: true
      };
      this.currentAnimations.set(instanceId, state);
      console.log(`Initialized animation state for ${animationName}, frames: ${animation.frames.length}, frameDuration: ${animation.frameDuration}`);
    }

    if (!state.playing) {
      return animation.frames[state.currentFrame] || animation.frames[0];
    }

    state.elapsed += validDeltaTime;

    if (state.elapsed >= animation.frameDuration) {
      const oldFrame = state.currentFrame;
      state.elapsed = 0; // Reset elapsed time completely
      state.currentFrame++;

      if (state.currentFrame >= animation.frames.length) {
        if (animation.loop) {
          state.currentFrame = 0;
        } else {
          state.currentFrame = animation.frames.length - 1;
          state.playing = false;
        }
      }

      // Only log frame changes occasionally to avoid spam
      if (Math.random() < 0.1) {
        console.log(`Animation ${animationName}: frame ${oldFrame} -> ${state.currentFrame} (${animation.frames.length} total)`);
      }
    }

    return animation.frames[state.currentFrame] || animation.frames[0];
  }

  public getCurrentFrame(instanceId: string, animationName: string): AnimationFrame | null {
    const animation = this.animations.get(animationName);
    const state = this.currentAnimations.get(instanceId);

    if (!animation || !state) {
      return animation ? animation.frames[0] : null;
    }

    return animation.frames[state.currentFrame];
  }
}

client/src/lib/game/rendering/CameraSyst.content:
export interface Camera {
  x: number;
  y: number;
  width: number;
  height: number;
}

export class CameraSystem {
  public x: number = 0;
  public y: number = 0;
  public width: number;
  public height: number;
  private followSpeed: number = 0.1;
  private smoothing: boolean = false;

  constructor(width: number, height: number) {
    this.width = width;
    this.height = height;
  }

  public update(targetX: number, targetY: number, deltaTime: number): void {
    // Center the camera on the target - use pixel-perfect positioning
    this.x = Math.floor(targetX - this.width / 2);
    this.y = Math.floor(targetY - this.height / 2);
  }

  public setSize(width: number, height: number): void {
    this.width = width;
    this.height = height;
  }

  public setFollowSpeed(speed: number): void {
    this.followSpeed = speed;
  }

  public setSmoothing(enabled: boolean): void {
    this.smoothing = enabled;
  }

  public worldToScreen(worldX: number, worldY: number): { x: number; y: number } {
    return {
      x: worldX - this.x,
      y: worldY - this.y
    };
  }

  public screenToWorld(screenX: number, screenY: number): { x: number; y: number } {
    return {
      x: screenX + this.x,
      y: screenY + this.y
    };
  }

  public isInView(worldX: number, worldY: number, objectWidth: number = 0, objectHeight: number = 0): boolean {
    return !(worldX + objectWidth < this.x || 
             worldX > this.x + this.width ||
             worldY + objectHeight < this.y || 
             worldY > this.y + this.height);
  }

  public getViewBounds(): { left: number; right: number; top: number; bottom: number } {
    return {
      left: this.x,
      right: this.x + this.width,
      top: this.y,
      bottom: this.y + this.height
    };
  }
}

