import { GameObject } from "../entities/Player";
import { Projectile } from "../weapons/Projectile";
import { Enemy } from "../entities/Enemy";

export class CollisionDetection {
  public checkCollision(obj1: GameObject, obj2: GameObject): boolean {
    const obj1Width = (obj1 as any).collisionWidth || obj1.width;
    const obj1Height = (obj1 as any).collisionHeight || obj1.height;
    const obj2Width = (obj2 as any).collisionWidth || obj2.width;
    const obj2Height = (obj2 as any).collisionHeight || obj2.height;

    return (
      obj1.x - obj1Width / 2 < obj2.x + obj2Width / 2 &&
      obj1.x + obj1Width / 2 > obj2.x - obj2Width / 2 &&
      obj1.y - obj1Height / 2 < obj2.y + obj2Height / 2 &&
      obj1.y + obj1Height / 2 > obj2.y - obj2Height / 2
    );
  }

  public getDistance(obj1: GameObject, obj2: GameObject): number {
    const dx = obj1.x - obj2.x;
    const dy = obj1.y - obj2.y;
    return Math.sqrt(dx * dx + dy * dy);
  }

  public static checkProjectileEnemyCollisions(projectiles: Projectile[], enemies: Enemy[]): { projectilesToRemove: number[], enemiesToRemove: number[], damageDealt: number } {
    const projectilesToRemove: number[] = [];
    const enemiesToRemove: number[] = [];
    let damageDealt = 0;

    for (let i = 0; i < projectiles.length; i++) {
      const projectile = projectiles[i];
      const isPiercing = (projectile as any).piercing;
      let hitCount = (projectile as any).hitCount || 0;
      const maxHits = (projectile as any).maxHits || 1;

      for (let j = 0; j < enemies.length; j++) {
        const enemy = enemies[j];

        if (this.checkCollision(projectile, enemy)) {
          // Deal damage to enemy
          enemy.takeDamage(projectile.damage);
          damageDealt += projectile.damage;

          // Remove enemy if dead
          if (enemy.health <= 0 && !enemiesToRemove.includes(j)) {
            enemiesToRemove.push(j);
          }

          // Handle piercing logic
          if (isPiercing) {
            hitCount++;
            (projectile as any).hitCount = hitCount;

            // Remove piercing projectile if max hits reached
            if (hitCount >= maxHits && !projectilesToRemove.includes(i)) {
              projectilesToRemove.push(i);
            }
          } else {
            // Remove regular projectile
            if (!projectilesToRemove.includes(i)) {
              projectilesToRemove.push(i);
            }
            break; // Regular projectile stops after first hit
          }
        }
      }
    }

    return { projectilesToRemove, enemiesToRemove, damageDealt };
  }
}

client/src/lib/game/systems/InputManager.content:
export interface InputState {
  left: boolean;
  right: boolean;
  up: boolean;
  down: boolean;
  mute: boolean;
  restart: boolean;
  weapon1: boolean;
  weapon2: boolean;
  weapon3: boolean;
  weapon4: boolean;
  weapon5: boolean;
}

export class InputManager {
  private keys: { [key: string]: boolean } = {};

  constructor() {
    this.keys = {
      left: false,
      right: false,
      up: false,
      down: false,
      mute: false,
      restart: false,
      weapon1: false,
      weapon2: false,
      weapon3: false,
      weapon4: false,
      weapon5: false
    };
  }

  public addEventListeners() {
    document.addEventListener("keydown", this.handleKeyDown);
    document.addEventListener("keyup", this.handleKeyUp);
  }

  public removeEventListeners() {
    document.removeEventListener("keydown", this.handleKeyDown);
    document.removeEventListener("keyup", this.handleKeyUp);
  }

  private handleKeyDown = (e: KeyboardEvent) => {
    this.keys[e.code] = true;
    // Prevent default behavior for game keys
    if (["KeyW", "KeyA", "KeyS", "KeyD", "ArrowUp", "ArrowDown", "ArrowLeft", "ArrowRight"].includes(e.code)) {
      e.preventDefault();
    }
  };

  private handleKeyUp = (e: KeyboardEvent) => {
    this.keys[e.code] = false;
  };

  public getInput() {
    return {
      up: this.keys["KeyW"] || this.keys["ArrowUp"],
      down: this.keys["KeyS"] || this.keys["ArrowDown"],
      left: this.keys["KeyA"] || this.keys["ArrowLeft"],
      right: this.keys["KeyD"] || this.keys["ArrowRight"],
      weapon1: this.keys["1"],
      weapon2: this.keys["2"],
      weapon3: this.keys["3"],
      weapon4: this.keys["4"],
      weapon5: this.keys["5"],
      mute: this.keys["m"] || this.keys["M"],
      restart: this.keys["r"] || this.keys["R"]
    };
  }

  private isKeyPressed(key: string): boolean {
    return this.keys[key] === true;
  }

  public update() {
    this.keys.left = this.isKeyPressed('ArrowLeft') || this.isKeyPressed('a') || this.isKeyPressed('A');
    this.keys.right = this.isKeyPressed('ArrowRight') || this.isKeyPressed('d') || this.isKeyPressed('D');
    this.keys.up = this.isKeyPressed('ArrowUp') || this.isKeyPressed('w') || this.isKeyPressed('W');
    this.keys.down = this.isKeyPressed('ArrowDown') || this.isKeyPressed('s') || this.isKeyPressed('S');
    this.keys.mute = this.isKeyPressed('m') || this.isKeyPressed('M');
    this.keys.restart = this.isKeyPressed('r') || this.isKeyPressed('R');

    // Debug keys for weapon testing
    this.keys.weapon1 = this.isKeyPressed('1');
    this.keys.weapon2 = this.isKeyPressed('2');
    this.keys.weapon3 = this.isKeyPressed('3');
    this.keys.weapon4 = this.isKeyPressed('4');
    this.keys.weapon5 = this.isKeyPressed('5');
  }
}

