import { GameObject } from "./Player";

export class OrbitalWeapon implements GameObject {
  public x: number = 0;
  public y: number = 0;
  public width = 12;
  public height = 12;
  private angle = 0;
  private radius = 50;
  private rotationSpeed = 2; // radians per second
  private damage = 3;
  private damageTimer = 0;
  private damageCooldown = 0.5; // seconds between damage ticks

  public update(deltaTime: number, playerX: number, playerY: number): { x: number; y: number } {
    this.angle += this.rotationSpeed * deltaTime;
    this.damageTimer += deltaTime;

    // Calculate orbital position
    this.x = playerX + Math.cos(this.angle) * this.radius;
    this.y = playerY + Math.sin(this.angle) * this.radius;

    return { x: this.x, y: this.y };
  }

  public render(ctx: CanvasRenderingContext2D, x: number, y: number) {
    // Draw orbital weapon as a spinning diamond
    ctx.save();
    ctx.translate(x, y);
    ctx.rotate(this.angle * 2);

    // Outer glow
    ctx.shadowColor = "#4444ff";
    ctx.shadowBlur = 10;
    ctx.fillStyle = "#4444ff";
    ctx.fillRect(-6, -6, 12, 12);

    // Inner core
    ctx.shadowBlur = 0;
    ctx.fillStyle = "#aaaaff";
    ctx.fillRect(-3, -3, 6, 6);

    ctx.restore();
  }

  public canDamage(): boolean {
    return this.damageTimer >= this.damageCooldown;
  }

  public dealDamage(): number {
    if (this.canDamage()) {
      this.damageTimer = 0;
      return this.damage;
    }
    return 0;
  }

  public getDamage(): number {
    return this.damage;
  }

  public upgrade() {
    this.damage += 1;
    this.rotationSpeed += 0.3;
    this.radius += 5;
  }
}

client/src/lib/game/weapons/Projectile.t.content:
import { GameObject } from "./Player";

export class Projectile implements GameObject {
  public x: number;
  public y: number;
  public width = 8;
  public height = 8;
  public vx: number;
  public vy: number;
  private damage: number;
  private alive = true;
  private lifetime = 8; // seconds
  private piercing = false;
  private hitCount = 0;
  private maxHits = 1;
  private isSylphOrb = false;

  constructor(x: number, y: number, vx: number, vy: number, damage: number) {
    this.x = x;
    this.y = y;
    this.vx = vx;
    this.vy = vy;
    this.damage = damage;
  }

  public update(deltaTime: number) {
    if (!this.alive) return;

    // Ensure deltaTime is valid and reasonable (cap at 1/30th second to prevent large jumps)
    const validDeltaTime = typeof deltaTime === 'number' && !isNaN(deltaTime) && deltaTime > 0 ? 
      Math.min(deltaTime, 1/30) : 0.016;

    // Update position
    this.x += this.vx * validDeltaTime;
    this.y += this.vy * validDeltaTime;

    // Debug logging for first few frames to verify movement
    if (this.lifetime > 2.9 && this.lifetime < 3.0) {
      console.log(`Projectile position: (${this.x.toFixed(1)}, ${this.y.toFixed(1)}), velocity: (${this.vx.toFixed(1)}, ${this.vy.toFixed(1)})`);
    }

    this.lifetime -= validDeltaTime;
    if (this.lifetime <= 0) {
      this.alive = false;
    }
  }

  public isAlive(): boolean {
    return this.alive;
  }

  public destroy() {
    this.alive = false;
  }

  public getDamage(): number {
    return this.damage;
  }

  public setSylphOrb(isSylph: boolean) {
    this.isSylphOrb = isSylph;
  }

  public isSylphOrbProjectile(): boolean {
    return this.isSylphOrb;
  }

  public isPiercing(): boolean {
    return this.piercing;
  }

  public setPiercing(piercing: boolean, maxHits: number = 3) {
    this.piercing = piercing;
    this.maxHits = maxHits;
  }

  public isSylph(): boolean {
    return this.isSylphOrb;
  }

  public addHit(): boolean {
    if (this.piercing) {
      this.hitCount++;
      if (this.hitCount >= this.maxHits) {
        this.destroy();
        return true; // Projectile should be destroyed
      }
      return false; // Projectile continues
    } else {
      this.destroy();
      return true; // Regular projectile is destroyed after one hit
    }
  }

  public render(ctx: CanvasRenderingContext2D, cameraX: number = 0, cameraY: number = 0): void {
    const screenX = this.x - cameraX;
    const screenY = this.y - cameraY;

    ctx.save();

    if (this.isSylphOrb) {
      // Render Sylph Orb with magical pink/purple glow
      const pulse = Math.sin(Date.now() * 0.01) * 0.3 + 0.7;

      // Outer glow
      ctx.shadowColor = "#e91e63";
      ctx.shadowBlur = 15;
      ctx.fillStyle = `rgba(233, 30, 99, ${pulse})`;
      ctx.beginPath();
      ctx.arc(screenX, screenY, this.width / 2 + 2, 0, Math.PI * 2);
      ctx.fill();

      // Inner core
      ctx.shadowBlur = 0;
      ctx.fillStyle = "#ffffff";
      ctx.beginPath();
      ctx.arc(screenX, screenY, this.width / 4, 0, Math.PI * 2);
      ctx.fill();

      // Sparkle trail effect
      if (Math.random() < 0.5) {
        ctx.fillStyle = `rgba(255, 255, 255, ${Math.random() * 0.8})`;
        ctx.beginPath();
        ctx.arc(
          screenX + (Math.random() - 0.5) * 10,
          screenY + (Math.random() - 0.5) * 10,
          1,
          0,
          Math.PI * 2
        );
        ctx.fill();
      }
    } else if (this.damage >= 1.5) {
      // Render magical emerald energy bolt - make it bigger
      // Outer glow
      ctx.shadowColor = "#50c878";
      ctx.shadowBlur = 12;
      ctx.fillStyle = "#50c878";
      ctx.beginPath();
      ctx.arc(screenX, screenY, 8, 0, Math.PI * 2);
      ctx.fill();

      // Inner core
      ctx.shadowBlur = 0;
      ctx.fillStyle = "#90ee90";
      ctx.beginPath();
      ctx.arc(screenX, screenY, 4, 0, Math.PI * 2);
      ctx.fill();

      // Sparkle effect
      ctx.fillStyle = "#ffffff";
      ctx.beginPath();
      ctx.arc(screenX - 2, screenY - 2, 1, 0, Math.PI * 2);
      ctx.fill();
    } else {
      // Regular projectile - make it much larger and more visible
      const size = 8; // Increased from 4

      // Bright yellow projectile
      ctx.fillStyle = "#ffff00";
      ctx.fillRect(
        screenX - size / 2,
        screenY - size / 2,
        size,
        size
      );

      // Add a bright white center
      ctx.fillStyle = "#ffffff";
      ctx.fillRect(
        screenX - 2,
        screenY - 2,
        4,
        4
      );

      // Add bright red outline for maximum visibility
      ctx.strokeStyle = "#ff0000";
      ctx.lineWidth = 2;
      ctx.strokeRect(
        screenX - size / 2,
        screenY - size / 2,
        size,
        size
      );

      // Add additional glow effect
      ctx.shadowColor = "#ffff00";
      ctx.shadowBlur = 6;
      ctx.fillStyle = "#ffff00";
      ctx.beginPath();
      ctx.arc(screenX, screenY, size / 2, 0, Math.PI * 2);
      ctx.fill();
    }

    ctx.restore();
  }
}

client/src/lib/game/weapons/SylphBloomsW.content:
import { Weapon } from "./Weapon";
import { Player } from "./Player";
import { Enemy } from "../entities/Enemy";
import { Particle } from "./Particle";
import { InfiniteTileRenderer } from "./InfiniteTileRenderer";



export class SylphBloomsWeapon {
  protected damage: number;
  protected fireRate: number;
  protected projectileSpeed: number;
  protected lastFireTime: number = 0;
  private lastFlowerSpawn: number = 0;
  private flowerSpawnInterval: number = 8000; // 8 seconds between flower spawns
  private maxFlowers: number = 4; // Maximum flowers on field
  private flowerLifespan: number = 15000; // 15 seconds lifespan
  private tileRenderer: InfiniteTileRenderer | null = null;

  constructor() {
    this.fireRate = 2;
    this.damage = 35;
    this.projectileSpeed = 150;
  }

  // SylphBloomsWeapon doesn't fire regular projectiles, only flower orbs
  public fire(
    deltaTime: number,
    playerX: number,
    playerY: number,
    direction?: { x: number; y: number },
  ): any[] {
    return []; // No regular projectiles
  }

  public setTileRenderer(tileRenderer: InfiniteTileRenderer): void {
    this.tileRenderer = tileRenderer;
  }

  update(
    deltaTime: number,
    enemies: Enemy[],
    playerX: number,
    playerY: number,
  ): void {
    if (!this.tileRenderer) return;

    const now = Date.now();

    // Spawn new flowers periodically
    const currentFlowers = this.tileRenderer.getAllFlowers();
    if (
      now - this.lastFlowerSpawn >= this.flowerSpawnInterval &&
      currentFlowers.length < this.maxFlowers
    ) {
      this.spawnFlowerTurret(playerX, playerY);
      this.lastFlowerSpawn = now;
    }

    // Update flowers through tile renderer
    this.tileRenderer.updateFlowers(deltaTime);

    // Check flower shooting
    const flowers = this.tileRenderer.getAllFlowers();
    flowers.forEach((flower) => {
      if (this.tileRenderer!.canFlowerShoot(flower)) {
        const tileSize = this.tileRenderer!.getTileSize();
        const flowerWorldX = flower.tileX * tileSize + Math.floor(tileSize / 2);
        const flowerWorldY = flower.tileY * tileSize + Math.floor(tileSize / 2);
        const nearestEnemy = this.findNearestEnemy(
          enemies,
          flowerWorldX,
          flowerWorldY,
          200,
        );
        if (nearestEnemy) {
          this.tileRenderer!.fireOrbFromFlower(flower, nearestEnemy, this.damage);
          this.tileRenderer!.flowerShoot(flower);
        }
      }
    });

    // Update orbs through tile renderer
    this.tileRenderer.updateOrbs(deltaTime, enemies, playerX, playerY);
  }

  private spawnFlowerTurret(playerX: number, playerY: number): void {
    if (!this.tileRenderer) return;

    const tileSize = this.tileRenderer.getTileSize();
    const playerTileX = Math.floor(playerX / tileSize);
    const playerTileY = Math.floor(playerY / tileSize);
    const maxTileRadius = 6;
    const minTileRadius = 3;

    const existingFlowers = this.tileRenderer.getAllFlowers();

    let foundPosition = false;
    let attempts = 0;
    let targetTileX = playerTileX;
    let targetTileY = playerTileY;

    while (!foundPosition && attempts < 50) {
      const angle = Math.random() * Math.PI * 2;
      const tileRadius =
        minTileRadius +
        Math.floor(Math.random() * (maxTileRadius - minTileRadius + 1));
      targetTileX = playerTileX + Math.round(Math.cos(angle) * tileRadius);
      targetTileY = playerTileY + Math.round(Math.sin(angle) * tileRadius);

      // Check if tile is already occupied
      const existingFlower = this.tileRenderer.getFlower(
        targetTileX,
        targetTileY,
      );
      if (!existingFlower) {
        // Check distance from other flowers
        let tooClose = false;
        for (const flower of existingFlowers) {
          const dx = flower.tileX - targetTileX;
          const dy = flower.tileY - targetTileY;
          const tileDistance = Math.sqrt(dx * dx + dy * dy);
          if (tileDistance < 2) {
            tooClose = true;
            break;
          }
        }

        if (!tooClose) {
          foundPosition = true;
        }
      }
      attempts++;
    }

    // Fallback position if no good spot found
    if (!foundPosition) {
      const angle = Math.random() * Math.PI * 2;
      const tileRadius = 4;
      targetTileX = playerTileX + Math.round(Math.cos(angle) * tileRadius);
      targetTileY = playerTileY + Math.round(Math.sin(angle) * tileRadius);
    }

    const flower = this.tileRenderer.addFlower(
      targetTileX,
      targetTileY,
      this.flowerLifespan,
    );
    if (flower) {
      const worldX = targetTileX * tileSize;
      const worldY = targetTileY * tileSize;
      console.log(`Spawned flower:`);
      console.log(`  Tile coords: (${targetTileX}, ${targetTileY})`);
      console.log(`  World coords: (${worldX}, ${worldY})`);
      console.log(
        `  Player coords: (${playerX.toFixed(1)}, ${playerY.toFixed(1)})`,
      );
      console.log(`  Player tile: (${playerTileX}, ${playerTileY})`);
    }
  }

  private findNearestEnemy(
    enemies: Enemy[],
    x: number,
    y: number,
    maxRange: number,
  ): Enemy | null {
    let nearest: Enemy | null = null;
    let nearestDistance = maxRange;

    enemies.forEach((enemy) => {
      if (enemy.health <= 0) return;

      const dx = enemy.x - x;
      const dy = enemy.y - y;
      const distance = Math.sqrt(dx * dx + dy * dy);

      if (distance < nearestDistance) {
        nearest = enemy;
        nearestDistance = distance;
      }
    });

    return nearest;
  }



  render(
    ctx: CanvasRenderingContext2D,
    cameraX: number,
    cameraY: number,
  ): void {
    // Flowers and orbs are now rendered by the tile system
    // This method is kept for interface compatibility but does nothing
  }

  checkCollisions(enemies: Enemy[]): { enemy: any; damage: number; orbX: number; orbY: number }[] {
    // Delegate collision checking to tile renderer and return results
    if (this.tileRenderer) {
      return this.tileRenderer.checkOrbCollisions(enemies);
    }
    return [];
  }

  getParticles(): Particle[] {
    return this.tileRenderer ? this.tileRenderer.getAllOrbs() : [];
  }

  // Upgrade methods for power-ups
  public upgradeDamage() {
    this.damage += 5;
  }

  public upgradeFireRate() {
    // Reduce flower spawn interval and shot cooldown
    this.flowerSpawnInterval = Math.max(2000, this.flowerSpawnInterval - 300);
    // Note: Individual flower shot cooldowns are handled by the tile renderer
  }

  public upgradeFlowerCapacity() {
    this.maxFlowers = Math.min(6, this.maxFlowers + 1);
  }

  public upgradeFlowerLifespan() {
    this.flowerLifespan += 3000; // +3 seconds
  }
}

